import React, { useState, useEffect } from 'react';
import { View, Text, Pressable, ScrollView, StyleSheet, Image } from 'react-native';
import { doc, getDoc, updateDoc } from 'firebase/firestore';
import {db} from '';
const InfoScreen = ({ route }) => {
  const [vehicle, setVehicle] = useState(null);

  useEffect(() => {
    const fetchVehicle = async () => {
      const docRef = doc(db, 'cars', route.params.id);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        setVehicle({ id: docSnap.id, ...docSnap.data() });
      } else {
        console.log('No such document!');
      }
    };

    fetchVehicle();
  }, [route.params.id]);

  const handleRentToggle = async () => {
    const newStatus = !vehicle.isRented;
    const docRef = doc(db, 'cars', vehicle.id);

    await updateDoc(docRef, {
      isRented: newStatus
    });

    setVehicle({ ...vehicle, isRented: newStatus });
  };

  if (!vehicle) {
    return <Text>Loading...</Text>;
  }
const back = require("../../assets/icons/left-arrow.png");
const dots = require("../../assets/icons/dots.png");

const image_v_1 = require("../../assets/vehicles/v-1.png");
const image_v_2 = require("../../assets/vehicles/v-2.png");
const image_v_3 = require("../../assets/vehicles/v-3.png");
const image_v_4 = require("../../assets/vehicles/v-4.png");

  const getImage = (id) => {
    if (id == 1) return image_v_1;
    if (id == 2) return image_v_2;
    if (id == 3) return image_v_3;
    if (id == 4) return image_v_4;
  };

  return (
    <ScrollView style={styles.scrollView}>
      <View style={styles.container}>
      <Image
            source={dots}
            resizeMode="contain"
            style={styles.faceIconStyle}
          />
        <Text style={styles.vehicleDetails}>
          {vehicle.make} {vehicle.model}
          {'\n'}Type: {vehicle.type}
          {'\n'}Transmission: {vehicle.transmission}
          {'\n'}Price per Day: ${vehicle.price_per_day}
          {'\n'}Description: {vehicle.description}
        </Text>
        <Pressable style={styles.rentButton} onPress={handleRentToggle}>
          <Text style={styles.rentButtonText}>{vehicle.isRented ? 'Unrent Car' : 'Rent a Car'}</Text>
        </Pressable>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollView: {
    flex: 1,
    backgroundColor: '#fff',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  vehicleImage: {
    width: '100%',
    height: 200,
    resizeMode: 'cover',
    marginBottom: 20,
  },
  vehicleDetails: {
    fontSize: 16,
    marginBottom: 20,
    textAlign: 'center',
  },
  rentButton: {
    backgroundColor: 'blue',
    padding: 10,
    borderRadius: 5,
  },
  rentButtonText: {
    color: 'white',
    fontSize: 16,
  },
  // Add more styles as needed
});

export default InfoScreen;
