// CheckoutScreen.js
import React from 'react';
import { View, Text, TouchableOpacity } from 'react-native';

const CheckoutScreen = ({ navigation, route }) => {
  // Extract car information from the route params
  const { make, model, price_per_day } = route.params;

  // Assuming you have a function to calculate the total cost
  const calculateTotal = () => {
    // Implement your logic to calculate the total cost
    // This can be based on the duration of the rental, for example
    const durationInDays = 5; // Replace with the actual duration
    return durationInDays * price_per_day;
  };

  const handleConfirm = async () => {
    // Implement logic to delete data from Firebase DB (Firestore)
    // You can use the car ID or any other identifier to delete the data
    // For example, if 'id' is the unique identifier, you can use:
    // await deleteDoc(doc(db, 'cars', id));

    // Display a success message
    alert('Car rented successfully!');

    // Navigate back to the HomeScreen or any other appropriate screen
    navigation.navigate('Home');
  };

  return (
    <View>
      <Text>Total: ${calculateTotal()}</Text>
      <Text>Car Info: {make} {model}</Text>
      <TouchableOpacity onPress={handleConfirm}>
        <Text>Confirm</Text>
      </TouchableOpacity>
    </View>
  );
};

export default CheckoutScreen;
