import React, { useState } from 'react';
import { StyleSheet, Text, View, ScrollView, TouchableOpacity } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Import Ionicons from Expo

const SettingsScreen = ({ navigation }) => {
  const [faqOpen, setFaqOpen] = useState(false);

  const toggleFaq = () => {
    setFaqOpen(!faqOpen);
  };

  const handleBackPress = () => {
    navigation.goBack();
  };

  return (
    <ScrollView
      style={{
        flex: 1,
        backgroundColor: "#e7e7e7",
        padding: 16,
      }}
    >
      <View style={styles.header}>
        <TouchableOpacity onPress={handleBackPress}>
          {/* Replace "Back" with the arrow icon */}
          <Ionicons name="ios-arrow-back" size={24} color="blue" style={styles.backButton} />
        </TouchableOpacity>
        <Text style={styles.headerText}>Settings</Text>
      </View>

      {/* Account Section */}
      <View style={styles.card}>
        <Text style={styles.cardHeader}>Account</Text>
        <Text style={styles.cardItem}>Change Password</Text>
        <Text style={styles.cardItem}>Update Profile</Text>
      </View>

      {/* Notifications Section */}
      <View style={styles.card}>
        <Text style={styles.cardHeader}>Notifications</Text>
        <Text style={styles.cardItem}>Push Notifications</Text>
        <Text style={styles.cardItem}>Email Notifications</Text>
      </View>

      {/* FAQ Section */}
      <View style={styles.card}>
        <TouchableOpacity onPress={toggleFaq} style={styles.faqHeader}>
          <Text style={styles.cardHeader}>FAQ</Text>
          <Text style={styles.arrow}>{faqOpen ? '▼' : '▶'}</Text>
        </TouchableOpacity>

        {faqOpen && (
          <View style={styles.faqContent}>
            <Text style={styles.faqItem}>
              Q: How do I rent a car?
              {'\n'}A: Simply go to the Home screen and browse available cars. Click on a car to view details and rent.
            </Text>
            <Text style={styles.faqItem}>
              Q: What is the rental duration?
              {'\n'}A: The rental duration is typically per day. You can check the specific details for each car.
            </Text>

            <Text style={styles.faqItem}>
              Q: What is the rental duration?
              {'\n'}A: The rental duration is typically per day. You can check the specific details for each car.
            </Text>

          </View>
        )}
      </View>

      {/* Online Reservation System Section */}
      <View style={styles.card}>
        <Text style={styles.cardHeader}>Online Reservation System</Text>
        <Text style={styles.cardItem}>
          A user-friendly and secure online reservation system.
        </Text>
        <Text style={styles.cardItem}>
          Clear instructions on how to make reservations, including dates, times, and pickup/drop-off locations.
        </Text>
      </View>


    </ScrollView>
  );
};

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  backButton: {
    fontSize: 24,
    marginRight: 10,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    marginLeft: 10,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 15,
    marginBottom: 15,
    elevation: 2, // for Android shadow
    shadowColor: '#000', // for iOS shadow
    shadowOffset: { width: 0, height: 2 }, // for iOS shadow
    shadowOpacity: 0.2, // for iOS shadow
  },
  cardHeader: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  cardItem: {
    fontSize: 16,
    marginBottom: 8,
  },
  faqHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  arrow: {
    fontSize: 18,
  },
  faqContent: {
    marginLeft: 15,
  },
  faqItem: {
    fontSize: 16,
    marginBottom: 12,
  },
});

export default SettingsScreen;
