import React, { useState } from 'react';
import { StyleSheet, Text, View, ScrollView, TouchableOpacity, FlatList } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Import Ionicons from Expo

const SettingsScreen = ({ navigation }) => {
  const [faqList, setFaqList] = useState([
    { id: '1', question: 'How do I rent a car?', answer: 'Simply go to the Home screen and browse available cars. Click on a car to view details and rent.' },
    { id: '2', question: 'What is the rental duration?', answer: 'The rental duration is typically per day. You can check the specific details for each car.' },
    { id: '3', question: 'Another FAQ', answer: 'Answer to the another question.' },
  ]);

  const [openFaqId, setOpenFaqId] = useState(null);

  const toggleFaq = (faqId) => {
    setOpenFaqId(openFaqId === faqId ? null : faqId);
  };

  const handleBackPress = () => {
    navigation.goBack();
  };

  const renderFaqItem = ({ item }) => (
    <TouchableOpacity onPress={() => toggleFaq(item.id)} style={styles.faqItemContainer}>
      <Text style={styles.faqQuestion}>{item.question}</Text>
      {openFaqId === item.id ? <Text style={styles.arrow}>▼</Text> : <Text style={styles.arrow}>▶</Text>}
      {openFaqId === item.id && <Text style={styles.faqAnswer}>{item.answer}</Text>}
    </TouchableOpacity>
  );

  return (
    <ScrollView style={styles.scrollView}>
      <View style={styles.container}>
        <View style={styles.header}>
          <TouchableOpacity onPress={handleBackPress}>
            <Ionicons name="ios-arrow-back" size={24} color="blue" style={styles.backButton} />
          </TouchableOpacity>
          <Text style={styles.headerText}>Settings</Text>
        </View>

        {/* Account Section */}
        <View style={styles.card}>
          <Text style={styles.cardHeader}>Account</Text>
          <Text style={styles.cardItem}>Change Password</Text>
          <Text style={styles.cardItem}>Update Profile</Text>
        </View>

        {/* Notifications Section */}
        <View style={styles.card}>
          <Text style={styles.cardHeader}>Notifications</Text>
          <Text style={styles.cardItem}>Push Notifications</Text>
          <Text style={styles.cardItem}>Email Notifications</Text>
        </View>
        <Text style={styles.cardHeader}>FAQ</Text>
        {/* FAQ Section */}
        <View style={styles.card}>
          <FlatList
            data={faqList}
            keyExtractor={(item) => item.id}
            renderItem={renderFaqItem}
          />
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollView: {
    flex: 1,
    backgroundColor: "#e7e7e7",
  },
  container: {
    flex: 1,
    padding: 16,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  backButton: {
    fontSize: 24,
    marginRight: 10,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
    marginLeft: 10,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 15,
    marginBottom: 15,
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
  },
  cardHeader: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  cardItem: {
    fontSize: 16,
    marginBottom: 8,
  },
  faqItemContainer: {
    marginBottom: 12,
  },
  faqQuestion: {
    fontSize: 16,
    marginBottom: 8,
  },
  arrow: {
    fontSize: 18,
    marginBottom: 8,
  },
  faqAnswer: {
    fontSize: 16,
    marginLeft: 15,
  },
});

export default SettingsScreen;
