import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, Pressable,StyleSheet } from 'react-native';
import { doc, getDocs, query, collection, where } from 'firebase/firestore';
import { db } from './firebase';

const CheckoutScreen = ({ navigation, route }) => {
  const { make, model, price_per_day } = route.params;
  const [carId, setCarId] = useState(null);

  useEffect(() => {
    const fetchCarId = async () => {
      try {
        const carsCollection = collection(db, 'cars');
        const querySnapshot = await getDocs(carsCollection);

        if (querySnapshot.size > 0) {
          // Replace the following line with your logic to identify the car
          const carDocument = querySnapshot.docs[0];

          // Assuming you have a unique identifier for the car
          const carId = carDocument.id;
          setCarId(carId);
        } else {
          console.error('Car not found.');
        }
      } catch (error) {
        console.error('Error fetching car ID:', error);
      }
    };

    fetchCarId();
  }, [make, model, price_per_day])

  const calculateTotal = () => {
    const durationInDays = 5; // Replace with the actual duration
    return durationInDays * price_per_day;
  };

  const handleConfirm = async () => {
    try {
      if (!carId) {
        console.error('Car ID is not available.');
        return;
      }

      await deleteDoc(doc(db, 'cars', carId));

      alert('Car rented successfully!');
      navigation.navigate('Home');
    } catch (error) {
      console.error('Error confirming rental:', error);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.infoBox}>
        <Text style={styles.totalText}>Total:</Text>
        <Text style={styles.totalAmount}>${calculateTotal()}</Text>
      </View>
      <View style={styles.infoBox}>
        <Text style={styles.carInfoText}>Car Info:</Text>
        <Text style={styles.carDetails}>{make} {model}</Text>
      </View>
      <Pressable style={styles.confirmButton} onPress={handleConfirm}>
        <Text style={styles.confirmButtonText}>Confirm</Text>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  infoBox: {
    marginBottom: 20,
  },
  totalText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  totalAmount: {
    fontSize: 24,
    color: 'green',
  },
  carInfoText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  carDetails: {
    fontSize: 16,
    color: 'blue',
  },
  confirmButton: {
    backgroundColor: 'orange',
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 10,
  },
  confirmButtonText: {
    fontSize: 18,
    color: 'white',
    textAlign: 'center',
  },
});

export default CheckoutScreen;
