// HomeScreen.js
import React, { useState, useEffect } from 'react';
import { View, Text, FlatList } from 'react-native';
import { collection, getDocs } from 'firebase/firestore';
import { db } from './firebase';

const HomeScreen = () => {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const carsCollection = collection(db, 'cars');
        const carsSnapshot = await getDocs(carsCollection);
        const carsData = carsSnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
        setCars(carsData);
      } catch (error) {
        console.error('Error fetching cars:', error);
      }
    };

    fetchCars();
  }, []);

  return (
    <View>
      <Text>Car List:</Text>
      <FlatList
        data={cars}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View>
            <Text>{item.make} {item.model}</Text>
            <Text>Type: {item.type}</Text>
            <Text>Transmission: {item.transmission}</Text>
            <Text>Price per Day: ${item.price_per_day}</Text>
            {/* Render other car information here */}
          </View>
        )}
      />
    </View>
  );
};

export default HomeScreen;
