// CheckoutScreen.js
import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { doc, deleteDoc, updateDoc } from 'firebase/firestore';
import { db } from './firebase';
const CheckoutScreen = ({ navigation, route }) => {
  // Extract car information from the route params
  const { make, model, price_per_day } = route.params;

  // Assuming you have a function to calculate the total cost
  const calculateTotal = () => {
    // Implement your logic to calculate the total cost
    // This can be based on the duration of the rental, for example
    const durationInDays = 5; // Replace with the actual duration
    return durationInDays * price_per_day;
  };

  const handleConfirm = async () => {
    // Implement logic to delete data from Firebase DB (Firestore)
    // You can use the car ID or any other identifier to delete the data
    // For example, if 'id' is the unique identifier, you can use:
    // await deleteDoc(doc(db, 'cars', id));

    // Display a success message
    alert('Car rented successfully!');

    // Navigate back to the HomeScreen or any other appropriate screen
    navigation.navigate('Home');
  };

  return (
    <View style={styles.container}>
      <View style={styles.infoBox}>
        <Text style={styles.totalText}>Total:</Text>
        <Text style={styles.totalAmount}>${calculateTotal()}</Text>
      </View>
      <View style={styles.infoBox}>
        <Text style={styles.carInfoText}>Car Info:</Text>
        <Text style={styles.carDetails}>{make} {model}</Text>
      </View>
      <TouchableOpacity style={styles.confirmButton} onPress={handleConfirm}>
        <Text style={styles.confirmButtonText}>Confirm</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  infoBox: {
    marginBottom: 20,
  },
  totalText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  totalAmount: {
    fontSize: 24,
    color: 'green',
  },
  carInfoText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  carDetails: {
    fontSize: 16,
    color: 'blue',
  },
  confirmButton: {
    backgroundColor: 'orange',
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 10,
  },
  confirmButtonText: {
    fontSize: 18,
    color: 'white',
    textAlign: 'center',
  },
});

export default CheckoutScreen;
